package Java.Fundamentals;

public class FEB21_DIV7_NM4P7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 0;
		int count = 0;
		for (N = 0; count<7; N++) {
			if (N % 6== 0) {

				int sum = N*6* N;
				System.out.println(sum);
				count++;
	}
		}
	}
}
	
