package Java.Fundamentals;

public class FEB21_First_3_4Np9_div_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 0;
		int count = 0;
		for (N = 0; count<3; N++) {
			if (N % 3 == 0) {

				int sum = 4 * N + 9;
				System.out.println(sum);
				count++;
			}
		}

	}

}
